document.addEventListener("turbolinks:load",function(){$(".contactForm").on("submit",function(t){$.ajax({type:"POST",url:this.action,data:$(this).serialize(),success:function(t){"success"==t?alert(""):$(".message-class").html("<span class='text-error marginl10' ><b>Not sent, please fill in the correct details..</b></span>")}}),t.preventDefault(),$(".contactForm").trigger("reset")})});